    public void getStartTrigrams() {
        var uniqueTrigrams = new HashSet<string>();
        foreach (var word in englishWords) {
            if (word.Length >= 4) {
                uniqueTrigrams.Add(word.Substring(0,3).ToLower());
            }
        }
        Debug.Log("\""+string.Join("\",\"", uniqueTrigrams)+"\"");
    }
    public void getMidTrigrams() {
        var uniqueTrigrams = new HashSet<string>();
        foreach (var word in englishWords) {
            if (word.Length >= 5) {
                for (int i = 1; i < word.Length-3; i++)
                {
                    uniqueTrigrams.Add(word.Substring(i,3).ToLower());
                }
            }
        }
        Debug.Log("\""+string.Join("\",\"", uniqueTrigrams)+"\"");
    }
    public void getEndTrigrams() {
        var uniqueTrigrams = new HashSet<string>();
        foreach (var word in englishWords) {
            if (word.Length >= 4) {
                uniqueTrigrams.Add(word.Substring(word.Length-3).ToLower());
            }
        }
        Debug.Log("\""+string.Join("\",\"", uniqueTrigrams)+"\"");
    }

    public static string randomText(int length) {
        //You may get unexpected results for lengths < 4
        try
        {
            //Beginning
            var text = trigramsBegin[UnityEngine.Random.Range(0, trigramsBegin.Count)];
            //Middle
            while (text.Length < length-1) {
                text += getNextPossibleCharacter(text, trigramsMid);
            }
            //End
            if (length>3) {
                text += getNextPossibleCharacter(text, trigramsEnd);
            }
            return text;
        }
        catch (ArgumentOutOfRangeException)
        {
            //There are no trigrams to finish the text we generated --> retry
            return randomText(length);
        }
    }

    public static string getNextPossibleCharacter(string text, List<string> trigrams) {
        List<string> matchingTrigrams = trigrams.Where(tri => tri.Substring(0,2)==text.Substring(text.Length-2)).ToList();
        var trigram = matchingTrigrams[UnityEngine.Random.Range(0, matchingTrigrams.Count)];
        return trigram.Substring(trigram.Length-1);
    }
